import React, { useState, useEffect } from "react";

const RoomTypeView = () => {
  const [roomTypes, setRoomTypes] = useState([]);
  const [showConfirm, setShowConfirm] = useState(false); // Trạng thái dialog
  const [selectedRoomId, setSelectedRoomId] = useState(null); // Lưu room muốn xoá

  // Fetch dữ liệu từ API
  useEffect(() => {
    fetch("http://localhost:8080/api/roomType")
      .then((res) => res.json())
      .then((data) => setRoomTypes(data))
      .catch((err) => console.error("Failed to fetch room types:", err));
  }, []);

  // Hàm mở dialog xác nhận
  const openConfirmDialog = (id) => {
    setSelectedRoomId(id);
    setShowConfirm(true);
  };

  // Hàm xác nhận xoá
  const handleDelete = () => {
    // Gửi yêu cầu xoá lên server
    fetch(`http://localhost:8080/api/roomType/${selectedRoomId}`, {
      method: "DELETE",
    })
      .then(() => {
        // Cập nhật lại danh sách room
        setRoomTypes((prev) =>
          prev.filter((room) => room.id !== selectedRoomId)
        );
        setShowConfirm(false); // Đóng dialog
        setSelectedRoomId(null);
      })
      .catch((err) => console.error("Failed to delete room type:", err));
  };

  // Hàm đóng dialog
  const closeDialog = () => {
    setShowConfirm(false);
    setSelectedRoomId(null);
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Room Types</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {roomTypes.map((room) => (
          <div
            key={room.id}
            className="p-4 border rounded-lg shadow-md flex flex-col"
          >
            <img
              src={room.images[0]}
              alt={room.name}
              className="w-full h-40 object-cover mb-2 rounded"
            />
            <h2 className="text-lg font-semibold">{room.name}</h2>
            <p className="text-gray-600 text-sm">{room.description}</p>
            <p className="text-gray-800 font-bold mt-2">{room.price} VND</p>
            <button
              onClick={() => openConfirmDialog(room.id)}
              className="mt-4 bg-red-500 hover:bg-red-600 text-white py-2 px-4 rounded"
            >
              Xoá
            </button>
          </div>
        ))}
      </div>

      {/* Dialog Xác Nhận Xoá */}
      {showConfirm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded-lg shadow-lg text-center">
            <h3 className="text-xl font-semibold mb-4">Xác nhận xoá</h3>
            <p className="mb-6">Bạn có chắc chắn muốn xoá phòng này không?</p>
            <div className="flex justify-center gap-4">
              <button
                onClick={handleDelete}
                className="bg-red-500 hover:bg-red-600 text-white py-2 px-4 rounded"
              >
                Xác nhận
              </button>
              <button
                onClick={closeDialog}
                className="bg-gray-300 hover:bg-gray-400 text-gray-800 py-2 px-4 rounded"
              >
                Huỷ
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RoomTypeView;
