import React, { useEffect, useState } from "react";
import { useParams, useLocation } from "react-router-dom";

const RoomDetails = () => {
  const { id } = useParams(); // Lấy id từ URL
  const location = useLocation();
  const [room, setRoom] = useState(location.state?.room || null); // Lấy room từ state nếu có

  useEffect(() => {
    if (!room) {
      // Nếu không có room từ state, fetch danh sách và tìm room theo id
      const fetchRooms = async () => {
        try {
          const response = await fetch("http://localhost:8080/api/roomType");
          const data = await response.json();
          const foundRoom = data.find((item) => item.id === parseInt(id));
          setRoom(foundRoom || null); // Set room nếu tìm thấy
        } catch (error) {
          console.error("Error fetching room data:", error);
        }
      };

      fetchRooms();
    }
  }, [id, room]);

  if (!room) {
    return <div>Loading room details...</div>;
  }

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white rounded-lg shadow-lg p-8 max-w-2xl">
        {/* Hiển thị tất cả hình ảnh */}
        <div className="flex flex-wrap gap-4">
          {room.images.map((image, index) => (
            <img
              key={index}
              src={image}
              alt={`${room.name} - ${index + 1}`}
              className="w-full h-64 object-cover rounded"
            />
          ))}
        </div>

        <h1 className="text-3xl font-bold text-gray-800 mt-4">{room.name}</h1>
        <p className="text-gray-600 mt-2">{room.description}</p>
        <p className="text-gray-800 mt-4 font-semibold">Price: {room.price}</p>
      </div>
    </div>
  );
};

export default RoomDetails;
