import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const SignIn = ({ setIsAuthenticated }) => { // Accept setIsAuthenticated as a prop
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    try {
      const response = await fetch("http://localhost:8080/api/userAccount/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username, password }),
      });

      if (response.ok) {
        const data = await response.json();
        const jwt = data.jwt;
        localStorage.setItem("jwt", jwt); // Store JWT in localStorage
        setIsAuthenticated(true); // Update authentication state
        navigate("/"); // Redirect to home
      } else {
        const errorData = await response.json();
        setError(errorData.message || "Login failed. Please try again.");
      }
    } catch (err) {
      setError("An error occurred. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      {/* Form structure remains the same */}
      <form onSubmit={handleLogin}>
        {/* Username and Password fields */}
        <button type="submit" disabled={loading}>
          {loading ? "Logging in..." : "Login"}
        </button>
      </form>
    </div>
  );
};

export default SignIn;
