import React from "react";

const Home = () => {
  const roomTypes = [
    {
      id: 1,
      name: "Deluxe Room",
      image: "https://via.placeholder.com/600x400",
      description: "Luxurious rooms with amazing views.",
    },
    {
      id: 2,
      name: "Standard Room",
      image: "https://via.placeholder.com/600x400",
      description: "Affordable yet comfortable.",
    },
    {
      id: 3,
      name: "Suite",
      image: "https://via.placeholder.com/600x400",
      description: "Spacious rooms for family vacations.",
    },
    {
      id: 4,
      name: "Presidential Suite",
      image: "https://via.placeholder.com/600x400",
      description: "Experience the epitome of luxury.",
    },
  ];

  return (
    <div className="py-12 bg-gray-50">
      <h2 className="text-4xl font-bold text-center text-gray-800 mb-12">
        Our Rooms
      </h2>

      {/* Grid Layout */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-8 px-6 md:px-12">
        {roomTypes.map((room) => (
          <div
            key={room.id}
            className="relative overflow-hidden rounded-lg shadow-lg group cursor-pointer"
          >
            {/* Room Image */}
            <img
              src={room.image}
              alt={room.name}
              className="w-full h-[250px] object-cover transition-transform duration-500 group-hover:scale-110"
            />

            {/* Overlay Effect */}
            <div className="absolute inset-0 bg-black bg-opacity-30 group-hover:bg-opacity-60 transition duration-300 flex flex-col items-center justify-center text-white p-4">
              <h3 className="text-2xl font-semibold">{room.name}</h3>
              <p className="mt-2 text-sm">{room.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
