import React, { useState } from 'react';
import './App.css';
import Header from './components/Header';
import Footer from './components/Footer';
import SignIn from './page/SignIn';
import SignUp from './page/SignUp';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);  // Simulate authentication state
  const [userName, setUserName] = useState('John Doe');  // Simulate logged-in user

  const socialLinks = [
    { name: 'Facebook', url: 'https://facebook.com' },
    { name: 'Twitter', url: 'https://twitter.com' },
    { name: 'LinkedIn', url: 'https://linkedin.com' }
  ];

  return (
    <div className="App">
      <Header isAuthenticated={isAuthenticated} userName={userName} />
      <main className="main-content">
        <h1>Welcome to MyApp</h1>
        <p>This is a sample application with a dynamic header and footer.</p>
        {/* Placeholder for conditional routes or content */}
        {!isAuthenticated ? <SignIn /> : <SignUp />}
      </main>
      <Footer socialLinks={socialLinks} />
    </div>
  );
}

export default App;
