import React, { useState } from "react";

const AddRoom = () => {
  const [roomTypeName, setRoomTypeName] = useState("");
  const [price, setPrice] = useState("");
  const [files, setFiles] = useState(null);

  const handleFileChange = (event) => {
    setFiles(event.target.files);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Check if roomTypeName and price are valid
    if (!roomTypeName || price <= 0) {
      alert("Please provide valid room type name and price");
      return;
    }

    const formData = new FormData();
    formData.append("roomTypeName", roomTypeName);
    formData.append("price", price);

    // Append all files to FormData
    for (let i = 0; i < files.length; i++) {
      formData.append("files", files[i]);
    }

    try {
      const response = await fetch("http://localhost:8080/api/roomType/add", {
        method: "POST",
        headers: {
          Authorization: "Basic " + btoa("tuantuan:tuantuan"), // Basic Auth if required
        },
        body: formData,
      });

      if (!response.ok) {
        const errorText = await response.text();
        alert("Error: " + errorText);
        return;
      }

      const data = await response.json();
      console.log("Room Type created successfully:", data);
      alert("Room type created successfully!");
    } catch (error) {
      console.error("Error creating room type:", error);
      alert("An error occurred while creating the room type.");
    }
  };

  return (
    <div>
      <h1>Add Room Type</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Room Type Name</label>
          <input
            type="text"
            value={roomTypeName}
            onChange={(e) => setRoomTypeName(e.target.value)}
          />
        </div>
        <div>
          <label>Price</label>
          <input
            type="number"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
        </div>
        <div>
          <label>Images</label>
          <input type="file" multiple onChange={handleFileChange} />
        </div>
        <button type="submit">Add Room Type</button>
      </form>
    </div>
  );
};

export default AddRoom;
