// src/components/CancelBooking.jsx
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const CancelInfo = () => {
  const bookingInfo = {
    bookingId: "AB123456",
    customerName: "Nguyễn Văn A",
    bookingDate: "2024-12-15",
    cancelDate: "2024-12-18",
    cancelReason: "Công việc đột xuất",
    status: "Đã hủy",
  };

  const [bookingData, setBookingData] = useState(null);
  const [error, setError] = useState(null);
  const {id} = useParams();

  useEffect(() =>{
    const fetchData = async () =>{
        try {
            const response = await fetch(`http://localhost:8080/api/booking/getBooking/${id}`,{
                method: "GET"
            })
    
            if(response.ok){
                setError()
            }
        } catch (error) {
            
        }
        
    }
  },[id])

  return (
    <div className="min-h-screen flex justify-center items-center bg-gray-100 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-2xl w-full bg-white shadow-lg rounded-lg p-8">
        <h2 className="text-2xl font-semibold text-center text-gray-800 mb-6">
          Your booking was cancelled
        </h2>

        <div className="space-y-4">
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-600">Booking Id</label>
              <div className="mt-1 text-lg text-gray-800">{bookingInfo.bookingId}</div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-600">Your Name</label>
              <div className="mt-1 text-lg text-gray-800">{bookingInfo.customerName}</div>
            </div>
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-600">Booking Date</label>
              <div className="mt-1 text-lg text-gray-800">{bookingInfo.bookingDate}</div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-600">Cancel Date</label>
              <div className="mt-1 text-lg text-gray-800">{bookingInfo.cancelDate}</div>
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-600">Lý do hủy</label>
            <div className="mt-1 text-lg text-gray-800">{bookingInfo.cancelReason}</div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-600">Status</label>
            <div className="mt-1 text-lg text-red-500">{bookingInfo.status}</div>
          </div>
        </div>

        <div className="mt-6 text-center">
          <button className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700">
            Quay lại trang chủ
          </button>
        </div>
      </div>
    </div>
  );
};

export default CancelInfo;
