import React, { useState, useEffect } from "react";

const AddRoom = () => {
  const [roomTypeName, setRoomTypeName] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState(""); // Thêm trường mô tả
  const [capacity, setCapacity] = useState(""); // Thêm trường sức chứa
  const [files, setFiles] = useState(null);
  const [roomTypes, setRoomTypes] = useState([]); // Dữ liệu từ API
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  // Lấy danh sách roomType từ API
  useEffect(() => {
    const fetchRoomTypes = async () => {
      try {
        const response = await fetch("http://localhost:8080/roomtype");
        if (!response.ok) throw new Error("Failed to fetch room types");

        const data = await response.json();
        console.log("Full API Response:", data);

        // Extract roomTypes from the _embedded property
        const roomTypes = data._embedded?.roomTypes || [];
        console.log("Extracted Room Types:", roomTypes);

        setRoomTypes(roomTypes); // Set the extracted roomTypes
      } catch (error) {
        console.error("Error fetching room types:", error);
        setErrorMessage(error.message);
      }
    };

    fetchRoomTypes();
  }, []);

  // File change handler
  const handleFileChange = (event) => {
    setFiles(event.target.files);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!roomTypeName || price <= 0 || !description || capacity <= 0) {
      setErrorMessage("Please provide all required fields.");
      return;
    }

    // Create a new FormData instance to handle form submission
    const formData = new FormData();

    // Append files if any
    if (files) {
      for (let i = 0; i < files.length; i++) {
        formData.append("files", files[i]);
      }
    }

    // Prepare the room type data as an object
    const roomTypeData = {
      price: parseInt(price, 10),
      roomTypeName,
      description,
      capacity: parseInt(capacity, 10),
    };

    // Append room type data as a JSON string to the formData
    formData.append("roomType", JSON.stringify(roomTypeData));

    try {
      // Send the formData to the backend
      const response = await fetch("http://localhost:8080/api/roomType/add", {
        method: "POST",
        headers: {
          // No need to set 'Content-Type' because FormData does this automatically
          Authorization: "Basic " + btoa("tuantuan:tuantuan"), // Add authorization if needed
        },
        body: formData, // Use FormData as the request body
      });

      // Handle non-OK responses
      if (!response.ok) {
        const errorText = await response.text();
        setErrorMessage("Error: " + errorText);
        return;
      }

      // Parse the successful response
      const result = await response.json();
      setSuccessMessage("Room type added successfully!");
      console.log("API Response:", result);

      // Reset form fields after successful submission
      setRoomTypeName("");
      setPrice("");
      setDescription("");
      setCapacity("");
      setFiles(null); // Clear selected files
    } catch (error) {
      setErrorMessage("An error occurred. Please try again later.");
      console.error("Submit Error:", error);
    }
  };




  return (
    <div className="max-w-lg mx-auto p-6 bg-white shadow-lg rounded-lg">
      <h2 className="text-2xl font-bold text-center text-gray-800 mb-6">Add Room Type</h2>

      {errorMessage && (
        <div className="bg-red-500 text-white p-3 rounded mb-4">
          <p>{errorMessage}</p>
        </div>
      )}

      {successMessage && (
        <div className="bg-green-500 text-white p-3 rounded mb-4">
          <p>{successMessage}</p>
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label htmlFor="roomTypeDropdown" className="block text-gray-700 font-semibold mb-2">
            Room Type Name
          </label>
          <select
            id="roomTypeDropdown"
            value={roomTypeName}
            onChange={(e) => setRoomTypeName(e.target.value)}
            className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
            required
          >
            <option value="" disabled>
              Select Room Type
            </option>
            {roomTypes.map((roomType, index) => (
              <option key={index} value={roomType.roomTypeName}>
                {roomType.roomTypeName}
              </option>
            ))}
          </select>
        </div>

        <div className="mb-4">
          <label htmlFor="price" className="block text-gray-700 font-semibold mb-2">
            Price
          </label>
          <input
            type="number"
            id="price"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="description" className="block text-gray-700 font-semibold mb-2">
            Description
          </label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
            required
          ></textarea>
        </div>

        <div className="mb-4">
          <label htmlFor="capacity" className="block text-gray-700 font-semibold mb-2">
            Capacity
          </label>
          <input
            type="number"
            id="capacity"
            value={capacity}
            onChange={(e) => setCapacity(e.target.value)}
            className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="files" className="block text-gray-700 font-semibold mb-2">
            Upload Images
          </label>
          <input
            type="file"
            id="files"
            multiple
            onChange={handleFileChange}
            className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
          />
        </div>

        <div className="flex justify-center">
          <button
            type="submit"
            className="bg-indigo-600 text-white px-6 py-2 rounded-md hover:bg-indigo-700 focus:outline-none"
          >
            Add Room Type
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddRoom;
