import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

const PaymentResult = () => {
  const location = useLocation(); // Hook to get the current URL
  const [transactionData, setTransactionData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const params = new URLSearchParams(location.search);

    // Fetch payment result from your backend
    const fetchData = async () => {
      try {
        const response = await fetch(
          `http://localhost:8080/api/payment/transaction?vnp_Amount=${params.get(
            "vnp_Amount"
          )}&vnp_BankCode=${params.get("vnp_BankCode")}&vnp_OrderInfo=${params.get(
            "vnp_OrderInfo"
          )}&vnp_ResponseCode=${params.get("vnp_ResponseCode")}`,
          {
            method: "GET",
          }
        );

        if (response.ok) {
          const data = await response.json();
          setTransactionData(data);
        } else {
          setError("Failed to fetch transaction data.");
        }
      } catch (err) {
        setError("An error occurred while fetching transaction data.");
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [location]);


  console.log(transactionData)
  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold text-center mb-4">Transaction Result</h1>

      {transactionData && (
        <h1> ${transactionData.bankCode}</h1>
      )}
    </div>
  );
};

export default PaymentResult;
