import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

const PaymentResult = () => {
  const location = useLocation(); // Hook để lấy URL hiện tại
  const [transactionData, setTransactionData] = useState({});

  useEffect(() => {
    // Lấy query parameters từ URL
    const params = new URLSearchParams(location.search);
    const fetchData = async () =>{
      const response = await fetch(`http://localhost:8080/api/payment/transaction?vnp_Amount=${params.get("vnp_Amount")}&bank_Code=${params.get("vnp_BankCode")}&vnp_OderInfo=${params.get("vnp_OrderInfo")}&vnp_ResponseCode=${params.get("vnp_ResponseCode")}`,{
        method: "GET"
      });
      if(response.ok){
        const data = await response.json();
        setTransactionData(data);
      }
    }

    fetchData

  }, [location]);

  return (
    <div>
      <h1>Transaction Result</h1>
      {transactionData.vnp_ResponseCode === "00" ? (
        <div>
          <h2 style={{ color: "green" }}>Payment Successful!</h2>
          <p>Amount: {transactionData.vnp_Amount} VND</p>
          <p>Transaction No: {transactionData.vnp_TransactionNo}</p>
          <p>Order Ref: {transactionData.vnp_OrderInfo}</p>
          <p>Bank Code: {transactionData.vnp_BankCode}</p>
        </div>
      ) : (
        <div>
          <h2 style={{ color: "red" }}>Payment Failed!</h2>
          <p>Response Code: {transactionData.vnp_ResponseCode}</p>
        </div>
      )}
    </div>
  );
};

export default PaymentResult;
