import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const ManageCustomer = () =>{
     const [currentPage, setCurrentPage] = useState(0);
      const [listUserAccount, setListUserAccount] = useState(null);
      const [isLoading, setIsLoading] = useState(false);
      const [error, setError] = useState(null);
      const pageSize = 10;
      const navigate = useNavigate();       
    
      const handlePageChange = (page) => {
        setCurrentPage(page);
      };
    
      useEffect(() => {
        const fetchData = async () => {
          setIsLoading(true);
          setError(null);
          try {
            const response = await fetch(
              `http://localhost:8080/api/userAccount/getAll?page=${currentPage}&size=${pageSize}`,
              {
                method: "GET",
                headers: {
                  Authorization: `Bearer ${localStorage.getItem("jwt")}`,
                },
              }
            );
    
            if (!response.ok) {
              throw new Error("Không thể tải dữ liệu");
            }
    
            const data = await response.json();
            setListUserAccount(data);
          } catch (error) {
            setError(error.message);
          } finally {
            setIsLoading(false);
          }
        };
    
        fetchData();
      }, [currentPage]);
    
      if (isLoading) {
        return <div className="text-center p-6">Loading Data...</div>;
      }
    
      if (error) {
        return (
          <div className="text-center p-6 text-red-500">
            Đã xảy ra lỗi: {error}
          </div>
        );
      }
    
      if (!listUserAccount) {
        return <div className="text-center p-6">No data.</div>;
      }
    
      const { content = [], pageable = {}, totalPages = 1 } = listUserAccount;
      const handleViewDetail = (id) =>{
        navigate(`/detail/${id}`)
      }
    
      return (
        <div className="min-h-screen bg-gray-100 p-6">
          <div className="max-w-6xl mx-auto bg-white shadow-lg rounded-lg">
            
            <footer className="p-4 border-t border-gray-200">
              <div className="flex justify-between items-center">
                <button
                  className={`px-4 py-2 bg-blue-500 text-white rounded ${
                    pageable.pageNumber === 0 ? "opacity-50 cursor-not-allowed" : ""
                  }`}
                  onClick={() => handlePageChange(pageable.pageNumber - 1)}
                  disabled={pageable.pageNumber === 0}
                >
                  Previous
                </button>
                <span>
                  Page {pageable.pageNumber + 1} / {totalPages}
                </span>
                <button
                  className={`px-4 py-2 bg-blue-500 text-white rounded ${
                    pageable.pageNumber === totalPages - 1
                      ? "opacity-50 cursor-not-allowed"
                      : ""
                  }`}
                  onClick={() => handlePageChange(pageable.pageNumber + 1)}
                  disabled={pageable.pageNumber === totalPages - 1}
                >
                  Next
                </button>
              </div>
            </footer>
          </div>
        </div>
      );
}
export default ManageCustomer;
