import React, { useContext, useState } from 'react';
import { AuthContext } from './AuthContext';

const YourBooking = () => {
  // Dummy data for bookings
  const bookings = [
    {
      bookingId: '1',
      checkInDate: '2024-12-20',
      checkOutDate: '2024-12-25',
      roomTypeName: 'Deluxe Room',
      quantityRoom: 2,
      status: 'Confirmed',
    },
    {
      bookingId: '2',
      checkInDate: '2024-12-22',
      checkOutDate: '2024-12-28',
      roomTypeName: 'Standard Room',
      quantityRoom: 1,
      status: 'Pending',
    },
    {
      bookingId: '3',
      checkInDate: '2024-12-15',
      checkOutDate: '2024-12-18',
      roomTypeName: 'Suite',
      quantityRoom: 1,
      status: 'Cancelled',
    },
  ];

const {isAuthenticated, user} = useContext(AuthContext);
const [bookingss, setBookings] = useState([]);
const [loading, setLoading] = useState(false);
const [error, setError] = use

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-2xl font-bold text-center mb-6">Your Bookings</h1>
      
      {/* If no bookings */}
      {bookings.length === 0 ? (
        <p className="text-center">You have no bookings yet.</p>
      ) : (
        <table className="min-w-full bg-white shadow-md rounded-lg overflow-hidden">
          <thead>
            <tr className="bg-blue-500 text-white">
              <th className="px-6 py-3 text-left">Booking ID</th>
              <th className="px-6 py-3 text-left">Check-in</th>
              <th className="px-6 py-3 text-left">Check-out</th>
              <th className="px-6 py-3 text-left">Room Type</th>
              <th className="px-6 py-3 text-left">Quantity</th>
              <th className="px-6 py-3 text-left">Status</th>
            </tr>
          </thead>
          <tbody>
            {/* Loop through bookings data */}
            {bookings.map((booking, index) => (
              <tr key={index} className="border-b">
                <td className="px-6 py-4">{booking.bookingId}</td>
                <td className="px-6 py-4">{new Date(booking.checkInDate).toLocaleDateString()}</td>
                <td className="px-6 py-4">{new Date(booking.checkOutDate).toLocaleDateString()}</td>
                <td className="px-6 py-4">{booking.roomTypeName}</td>
                <td className="px-6 py-4">{booking.quantityRoom}</td>
                <td className="px-6 py-4">
                  <span
                    className={`px-2 py-1 rounded ${
                      booking.status === 'Confirmed' ? 'bg-green-200 text-green-800' : booking.status === 'Pending' ? 'bg-yellow-200 text-yellow-800' : 'bg-red-200 text-red-800'
                    }`}
                  >
                    {booking.status}
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default YourBooking;
