import React, { useState, useEffect } from "react";
import axios from "axios";

const RoomTypeView = () => {
  const [rooms, setRooms] = useState([]);
  const [selectedRoomType, setSelectedRoomType] = useState(null);

  // Fetch rooms from API
  useEffect(() => {
    axios.get("http://localhost:8080/room")
      .then((res) => {
        const roomsData = res.data._embedded?.rooms || []; // Lấy danh sách rooms
        console.log("Rooms API response:", roomsData);
        setRooms(roomsData);
      })
      .catch((error) => console.error("Error fetching rooms:", error));
  }, []);

  // Lọc các phòng theo RoomType
  const getRoomsByRoomType = (roomTypeId) => {
    if (!Array.isArray(rooms)) {
      console.error("Rooms is not an array:", rooms);
      return [];
    }
    return rooms.filter((room) => {
      // Thực hiện logic lọc roomTypeId (ở đây roomType là API phụ)
      return room._links.roomType.href.includes(`/roomType/${roomTypeId}`);
    });
  };

  return (
    <div>
      <h1>Room Types</h1>

      {/* Ví dụ: Chọn loại phòng */}
      <button onClick={() => setSelectedRoomType(1)}>Show Rooms for Type 1</button>
      <button onClick={() => setSelectedRoomType(2)}>Show Rooms for Type 2</button>

      {selectedRoomType && (
        <div>
          <h2>Rooms for RoomType {selectedRoomType}</h2>
          <div>
            {getRoomsByRoomType(selectedRoomType).map((room) => (
              <div key={room.roomId}>
                <h3>Room {room.roomNumber}</h3>
                <p>{room.description}</p>
                <p>Status: {room.status}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default RoomTypeView;
