import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const UpdateRoomType = () => {
  const [roomTypeName, setRoomTypeName] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");
  const [capacity, setCapacity] = useState("");
  const [files, setFiles] = useState(null);
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [roomTypeData, setRoomTypeData] = useState(null);
  const {id} = useParams();

  const handleFileChange = (event) => {
    setFiles(event.target.files);
  };


  useEffect(() => {
    const fetchData = async (id) =>{
        try {
            const response = await fetch(`http://localhost:8080/api/getRoomType/${id}`,{
                method: "GET"
            })
    
            if(response.ok){
                const data = await response.json();
                setRoomTypeData(data)
            }
        } catch (error) {
            
        }
    }
    fetchData();
  },[id])

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!roomTypeName || price <= 0 || !description || capacity <= 0) {
      setErrorMessage("Please provide all required fields.");
      return;
    }

    const formData = new FormData();

    // Append files if any
    if (files) {
      for (let i = 0; i < files.length; i++) {
        formData.append("files", files[i]);
      }
    }

    // Prepare room type data
    const roomTypeData = {
      price: parseInt(price, 10),
      roomTypeName,
      description,
      capacity: parseInt(capacity, 10),
    };

    // Append room type data as a Blob (JSON)
    formData.append("roomType", new Blob([JSON.stringify(roomTypeData)], { type: "application/json" }));

    try {
      const response = await fetch("http://localhost:8080/api/roomType/add", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${localStorage.getItem('jwt')}`
        },
        body: formData, // Use FormData as the request body
      });

      if (!response.ok) {
        const errorText = await response.text();
        setErrorMessage("Error: " + errorText);
        return;
      }

      const result = await response.json();
      setSuccessMessage("Room type added successfully!");
      console.log("API Response:", result);

      // Reset form fields after success
      setRoomTypeName("");
      setPrice("");
      setDescription("");
      setCapacity("");
      setFiles(null);
    } catch (error) {
      setErrorMessage("An error occurred. Please try again later.");
      console.error("Submit Error:", error);
    }
  };

  return (
    <div className="max-w-lg mx-auto p-6 bg-white shadow-lg rounded-lg">
      <h2 className="text-2xl font-bold text-center text-gray-800 mb-6">Add Room Type</h2>

      {errorMessage && (
        <div className="bg-red-500 text-white p-3 rounded mb-4">
          <p>{errorMessage}</p>
        </div>
      )}

      {successMessage && (
        <div className="bg-green-500 text-white p-3 rounded mb-4">
          <p>{successMessage}</p>
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label htmlFor="roomTypeName" className="block text-gray-700 font-semibold mb-2">
            Room Type Name
          </label>
          <input
            type="text"
            id="roomTypeName"
            value={roomTypeData}
            onChange={(e) => setRoomTypeName(e.target.value)}
            className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
            placeholder="Enter Room Type Name"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="price" className="block text-gray-700 font-semibold mb-2">
            Price
          </label>
          <input
            type="number"
            id="price"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="description" className="block text-gray-700 font-semibold mb-2">
            Description
          </label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
            required
          ></textarea>
        </div>

        <div className="mb-4">
          <label htmlFor="capacity" className="block text-gray-700 font-semibold mb-2">
            Capacity
          </label>
          <input
            type="number"
            id="capacity"
            value={capacity}
            onChange={(e) => setCapacity(e.target.value)}
            className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="files" className="block text-gray-700 font-semibold mb-2">
            Upload Images
          </label>
          <input
            type="file"
            id="files"
            multiple
            onChange={handleFileChange}
            className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
          />
        </div>

        <div className="flex justify-center">
          <button
            type="submit"
            className="bg-indigo-600 text-white px-6 py-2 rounded-md hover:bg-indigo-700 focus:outline-none"
          >
            Add Room Type
          </button>
        </div>
      </form>
    </div>
  );
};

export default UpdateRoomType;
