import React from 'react';

const Header = () => {
  return (
    <header className="flex flex-col md:flex-row justify-between items-center bg-gradient-to-r from-gray-700 via-gray-900 to-black p-6 md:p-4 shadow-md">
      <div className="flex items-center mb-4 md:mb-0">
        <h1 className="text-white text-2xl font-extrabold tracking-wide">CodeWithMe</h1>
      </div>
      <nav className="space-x-4 md:space-x-8 text-center">
        <a href="/" className="text-gray-300 hover:text-white hover:underline transition-all duration-300">Home</a>
        <a href="/Home" className="text-gray-300 hover:text-white hover:underline transition-all duration-300">About</a>
        <a href="/Rooms " className="text-gray-300 hover:text-white hover:underline transition-all duration-300">Services</a>
        <a href="/Booking" className="text-gray-300 hover:text-white hover:underline transition-all duration-300">Contact</a>
        <a href="/Contact us" className="text-gray-300 hover:text-white hover:underline transition-all duration-300">Contact</a>
      </nav>
      <button className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-full mt-4 md:mt-0 shadow-lg transform hover:scale-105 transition-transform duration-300">
        Sign In
      </button>
    </header>
  );
};

export default Header;
